INSERT INTO `renatio_formbuilder_field_types` (`id`, `name`, `description`, `code`, `markup`, `created_at`, `updated_at`) VALUES
(1, 'Text', 'Renders a single line text box.', 'text', '<div class=\"{{ wrapper_class }} form-group\">\r\n    {% if label %}\r\n        <label for=\"{{ name }}\" class=\"{{ label_class }}\">{{ label }}</label>\r\n    {% endif %}\r\n\r\n    {# Assign the input value from URL query parameter to a temporary variable #}\r\n    {% set query_value = input(name) %}\r\n\r\n    <input\r\n        id=\"{{ name }}\"\r\n        type=\"text\"\r\n        name=\"{{ name }}\"\r\n        value=\"{{ query_value ?? default }}\"\r\n        class=\"{{ class }} form-control\"\r\n        placeholder=\"{{ placeholder }}\"\r\n        {{ custom_attributes|raw }}\r\n    >\r\n\r\n    {% if comment %}\r\n        <p class=\"help-block\">{{ comment|raw }}</p>\r\n    {% endif %}\r\n</div>', '2022-10-01 14:51:21', '2025-04-30 08:04:47'),
(2, 'Textarea', 'Renders a multiline text box.', 'textarea', '<div class=\"{{ wrapper_class }} form-group\">\n    {% if label %}\n        <label for=\"{{ name }}\" class=\"{{ label_class }}\">{{ label }}</label>\n    {% endif %}\n\n    <textarea\n        id=\"{{ name }}\"\n        name=\"{{ name }}\"\n        class=\"{{ class }} form-control\"\n        placeholder=\"{{ placeholder }}\"\n        {{ custom_attributes|raw }}\n    >{{ default }}</textarea>\n\n    {% if comment %}\n        <p class=\"help-block\">{{ comment|raw }}</p>\n    {% endif %}\n</div>', '2022-10-01 14:51:21', '2022-10-01 14:51:21'),
(3, 'Dropdown', 'Renders a dropdown with specified options.', 'dropdown', '<div class=\"{{ wrapper_class }} form-group\">\n    {% if label %}\n        <label for=\"{{ name }}\" class=\"{{ label_class }}\">{{ label }}</label>\n    {% endif %}\n\n    <select\n        id=\"{{ name }}\"\n        class=\"{{ class }} form-control\"\n        name=\"{{ name }}\"\n        {{ custom_attributes|raw }}>\n        {% if placeholder %}\n            <option value=\"\">{{ placeholder }}</option>\n        {% endif %}\n\n        {% for key, label in options %}\n            <option\n                value=\"{{ key }}\"\n                {{ key == default ? \'selected\' : \'\' }}>\n                {{ label }}\n            </option>\n        {% endfor %}\n    </select>\n\n    {% if comment %}\n        <p class=\"help-block\">{{ comment|raw }}</p>\n    {% endif %}\n</div>', '2022-10-01 14:51:21', '2022-10-01 14:51:21'),
(4, 'Checkbox', 'Renders a single checkbox.', 'checkbox', '<div class=\"{{ wrapper_class }} checkbox form-group\">\n    <label for=\"{{ name }}\" class=\"{{ label_class }}\">\n        <input\n            id=\"{{ name }}\"\n            name=\"{{ name }}\"\n            class=\"{{ class }}\"\n            value=\"1\"\n            type=\"checkbox\"\n            {{ default ? \'checked\' : \'\' }}\n            {{ custom_attributes|raw }}\n        > {{ label }}\n    </label>\n\n    {% if comment %}\n        <p class=\"help-block\">{{ comment|raw }}</p>\n    {% endif %}\n</div>', '2022-10-01 14:51:21', '2022-10-01 14:51:21'),
(5, 'Checkbox List', 'Renders a list of checkboxes.', 'checkbox_list', '<div class=\"{{ wrapper_class }} form-group\">\n    {% if label %}\n        <label class=\"{{ label_class }}\">{{ label }}</label>\n    {% endif %}\n\n    {% for key, label in options %}\n        <div class=\"checkbox\">\n            <label for=\"{{ key }}\">\n                <input\n                    id=\"{{ key }}\"\n                    type=\"checkbox\"\n                    name=\"{{ name }}[]\"\n                    class=\"{{ class }}\"\n                    value=\"{{ key }}\"\n                    {{ default == key ? \'checked\' : \'\' }}\n                    {{ custom_attributes|raw }}\n                >\n                {{ label }}\n            </label>\n        </div>\n    {% endfor %}\n\n    {% if comment %}\n        <p class=\"help-block\">{{ comment|raw }}</p>\n    {% endif %}\n</div>', '2022-10-01 14:51:21', '2022-10-01 14:51:21'),
(6, 'Radio List', 'Renders a list of radio options, where only one item can be selected at a time.', 'radio_list', '<div class=\"{{ wrapper_class }} form-group {{ name }}\" data-list-for=\"{{ name }}\">\r\n    {% if label %}\r\n        <label class=\"{{ label_class }}\">{{ label }}</label>\r\n    {% endif %}\r\n\r\n    {% for key, label in options %}\r\n        <div class=\"radio\">\r\n            <label for=\"{{ key }}\">\r\n                <input\r\n                    id=\"{{ key }}\"\r\n                    type=\"radio\"\r\n                    name=\"{{ name }}\"\r\n                    class=\"{{ class }}\"\r\n                    value=\"{{ key }}\"\r\n                    {{ default == key ? \'checked\' : \'\' }}\r\n                    {{ custom_attributes|raw }}\r\n                >\r\n                {{ label }}\r\n            </label>\r\n        </div>\r\n    {% endfor %}\r\n\r\n    {% if comment %}\r\n        <p class=\"help-block\">{{ comment|raw }}</p>\r\n    {% endif %}\r\n</div>', '2022-10-01 14:51:22', '2023-04-19 04:46:28'),
(7, 'ReCaptcha', 'Renders a reCaptcha box.', 'recaptcha', '<div class=\"{{ wrapper_class }} form-group\">\n    {% if label %}\n        <label class=\"{{ label_class }}\">{{ label }}</label>\n    {% endif %}\n\n    <div\n        class=\"g-recaptcha\"\n        data-sitekey=\"{{ settings.site_key }}\"\n        data-theme=\"{{ settings.theme }}\"></div>\n\n    <script type=\"text/javascript\" src=\"https://www.google.com/recaptcha/api.js?hl={{ settings.lang }}\"></script>\n\n    {% if comment %}\n        <p class=\"help-block\">{{ comment|raw }}</p>\n    {% endif %}\n</div>', '2022-10-01 14:51:22', '2022-10-01 14:51:22'),
(8, 'Submit', 'Renders a submit button.', 'submit', '<div class=\"{{ wrapper_class }}\">\n    <button\n        type=\"submit\"\n        class=\"{{ class }} btn btn-primary\"\n        {{ custom_attributes|raw }}\n    >{{ label }}</button>\n</div>', '2022-10-01 14:51:22', '2022-10-01 14:51:22'),
(9, 'Country select', 'Renders a dropdown with country options.', 'country_select', '{% if location_plugin_enabled %}\n    <div class=\"{{ wrapper_class }} form-group\">\n        {% if label %}\n            <label for=\"{{ name }}\" class=\"{{ label_class }}\">{{ label }}</label>\n        {% endif %}\n\n        {{ form_select_country(name, null, {\n            id: name,\n            class: class ~ \' form-control\',\n            emptyOption: placeholder,\n            \'data-request\': \'onChangeCountry\',\n            \'data-request-data\': { placeholder: placeholder, name: name },\n        }) }}\n\n        {% if comment %}\n            <p class=\"help-block\">{{ comment|raw }}</p>\n        {% endif %}\n    </div>\n{% else %}\n    <div class=\"alert alert-warning\" role=\"alert\">\n        You must install <a target=\"_blank\" href=\"https://octobercms.com/plugin/rainlab-location\">Location Plugin</a> to use location fields!\n    </div>\n{% endif %}', '2022-10-01 14:51:22', '2022-10-01 14:51:22'),
(10, 'State select', 'Renders a dropdown with state options.', 'state_select', '{% if location_plugin_enabled %}\n    <div class=\"{{ wrapper_class }} form-group\">\n        {% if label %}\n            <label for=\"{{ name }}\" class=\"{{ label_class }}\">{{ label }}</label>\n        {% endif %}\n\n        {{ form_select_state(name, null, null, {\n            id: name,\n            class: class ~ \' form-control state-select\',\n            emptyOption: placeholder\n        }) }}\n\n        {% if comment %}\n            <p class=\"help-block\">{{ comment|raw }}</p>\n        {% endif %}\n    </div>\n{% else %}\n    <div class=\"alert alert-warning\" role=\"alert\">\n        You must install <a target=\"_blank\" href=\"https://octobercms.com/plugin/rainlab-location\">Location Plugin</a> to use location fields!\n    </div>\n{% endif %}', '2022-10-01 14:51:22', '2022-10-01 14:51:22'),
(11, 'File uploader', 'Renders a file uploader for regular files.', 'file_uploader', NULL, '2022-10-01 14:51:22', '2022-10-01 14:51:22'),
(12, 'Image uploader', 'Renders a image uploader for image files.', 'image_uploader', NULL, '2022-10-01 14:51:22', '2022-10-01 14:51:22'),
(13, 'Section', 'Renders a section heading and subheading.', 'section', '<div class=\"{{ wrapper_class }} field-section\">\n    <h4 class=\"{{ class }}\">{{ label }}</h4>\n\n    {% if comment %}\n        <p class=\"help-block\">{{ comment|raw }}</p>\n    {% endif %}\n</div>', '2022-10-01 14:51:22', '2022-10-01 14:51:22'),
(14, 'Phone number', 'Input type tel with patern pattern=\"^\\+?\\d{1,15}$\" +31727831183', 'phone-number', '<div class=\"{{ wrapper_class }} form-group\">\r\n    {% if label %}\r\n        <label for=\"{{ name }}\" class=\"{{ label_class }}\">{{ label }}</label>\r\n    {% endif %}\r\n\r\n    <input\r\n        id=\"phoneNumber\" \r\n        type=\"tel\"\r\n        name=\"{{ name }}\"\r\n        value=\"{{ default }}\"\r\n        class=\"{{ class }} form-control\"\r\n        placeholder=\"{{ placeholder }}\"\r\n        pattern=\"^\\+?\\d{1,15}$\"\r\n        {{ custom_attributes|raw }}\r\n    >\r\n\r\n    {% if comment %}\r\n        <p class=\"help-block\">{{ comment|raw }}</p>\r\n    {% endif %}\r\n</div>', '2023-04-17 07:11:24', '2023-04-17 07:11:33');
